//隐藏的公共函数   
function hidecommon(id){
    $('#'+id).hide();
}
//show公共函数
function showcommon(id,mydiv){
    $('#'+id).show();
    $('#'+mydiv).hide();
}



var alertsHtml = '<div class="weui_dialog_alert" id="dialog2" style="display: none;">';
alertsHtml += '<div class="weui-mask"></div>';
alertsHtml += '<div class="weui-dialog">';
alertsHtml += '<div class="weui-dialog__bd">弹窗内容</div>';
alertsHtml += '<div class="weui-dialog__ft">';
alertsHtml += '<a href="javascript:hidecommon("dialog2");" class="weui-dialog__btn weui-dialog__btn_primary">确定</a>';
alertsHtml += '</div></div></div>';
function alerts(text,url){
    if ($("#dialog2").length == 0) {
        $(document.body).append(alertsHtml);
    }

    $('#dialog2').show();
    $('#dialog2 .weui-dialog__bd').html(text);
    
    if(url=="" || url==null){
        $('#dialog2 .weui-dialog__btn_primary').attr("href",'javascript:hidecommon("dialog2");');
    }else{
        $('#dialog2 .weui-dialog__btn_primary').attr("href",url);
    }
}


var DialogHtml = '<div class="js_dialog" id="Dialog1" style="display: none;" >';
DialogHtml += '<div class="weui-mask"></div>';
DialogHtml += '<div class="weui-dialog">';
DialogHtml += '<div class="weui-dialog__hd"><strong class="weui-dialog__title">温馨提示</strong></div>';
DialogHtml += '<div class="weui-dialog__bd">弹窗内容</div>';
DialogHtml += '<div class="weui-dialog__ft">';
DialogHtml += '<a href="javascript:hidecommon(\'Dialog1\');" class="weui-dialog__btn weui-dialog__btn_default">取消</a>';
DialogHtml += '<a href="javascript:hidecommon(\'Dialog1\');" class="weui-dialog__btn weui-dialog__btn_primary">确定</a>';
DialogHtml += '</div></div></div>';
function dialog(text,primaryurl,defaulturl,title){
    if ($("#Dialog1").length == 0) {
        $(document.body).append(DialogHtml);
    }

    $('#Dialog1').show();
    $('#Dialog1 .weui-dialog__bd').html(text);
    
    if(title=="" || title==null){
        $('#Dialog1 .weui-dialog__title').html('温馨提示');
    }else{
        $('#Dialog1 .weui-dialog__title').html(title);
    }
    if(defaulturl=="" || defaulturl==null){
        $('#Dialog1 .weui-dialog__btn_default').attr("href",'javascript:hidecommon("Dialog1");');
    }else{
        $('#Dialog1 .weui-dialog__btn_default').attr("href",defaulturl);
    }
    if(primaryurl=="" || primaryurl==null){
        $('#Dialog1 .weui-dialog__btn_primary').attr("href",'javascript:hidecommon("Dialog1");');
    }else{
        $('#Dialog1 .weui-dialog__btn_primary').attr("href",primaryurl);
    }
}




var loadingToastHtml = '<div id="loadingToast" style="display: none;">';
loadingToastHtml += '<div class="weui-mask_transparent"></div>';
loadingToastHtml += '<div class="weui-toast">';
loadingToastHtml += '<i class="weui-loading weui-icon_toast"></i>';
loadingToastHtml += '<p class="weui-toast__content">数据加载中</p>';
loadingToastHtml += '</div></div>';
function loadingToast(text){
     if ($("#loadingToast").length == 0) {
        $(document.body).append(loadingToastHtml);
    }

    $('#loadingToast').show();
    if(text=="" || text==null){
        $('#loadingToast .weui-toast__content').html('数据加载中');
    }else{
        $('#loadingToast .weui-toast__content').html(text);
    }
}


function loadingToasthide(){
    $('#loadingToast').hide();
}










